
public class Ifelse {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
        int i = 7;
        if(i==0) {
        	System.out.println("nothing");
        }
        else
        if(i%2==0) {
        	System.out.println("even");
        }
        else
        	{
        		System.out.println("odd");
        	}
        }
	
	
	

}
